var tool=function(){};tool.prototype={ping:function(b){var a=new Image();a.ping_checked=false;a.onload=function(){if(!a.ping_checked){console.log(b+" is alive in",(new Date().getTime())-a.start,"ms")}a.ping_checked=true};a.onerror=function(g,d,c,f,e){if(!a.ping_checked){console.log(b+" is alive in",(new Date().getTime())-a.start,"ms")}a.ping_checked=true};a.start=new Date().getTime();a.src="//"+b;if(!a.ping_checked){this.timer=setTimeout(function(){if(!a.ping_checked){console.log(b+" unreachable in",(new Date().getTime())-a.start,"ms")}a.ping_checked=true},1500)}return""}};window.tool=new tool();